//MethodLocalInnerClassDemo3.java
package com.nt.methodlocalinnerclass;

public class MethodLocalInnerClassDemo3 {
	public void m1() {
	final	 int x = 10;
		class Inner {
			public void m2() {
				System.out.println(x);
			}// inner
		}// m1()
		Inner inner = new Inner();
		inner.m2();
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		MethodLocalInnerClassDemo3 methodLocalInnerClassDemo3 = new MethodLocalInnerClassDemo3();
		methodLocalInnerClassDemo3.m1();
	}

}

